mapper.update:
UPDATE EH_BOARD SET
BOARD_READ_COUNT = BOARD_READ_COUNT + 1
WHERE BOARD_NO = #{boardNo}

mapper.insert:
INSERT INTO EH_BOARD_ATTACHMENT SELECT SEQ_BO_ANO.NEXTVAL, A.* FROM (
SELECT #{item.thumbnailFilePath} BOARD_AT_PATH, #{item.fileName} BOARD_AT_NM, #{item.fileLevel} BOARD_AT_LEVEL, #{item.boardNo} BOARD_NO FROM DUAL
) A

mapper.update:
UPDATE EH_BOARD_ATTACHMENT SET
BOARD_AT_PATH = #{thumbnailFilePath},
BOARD_AT_NM = #{fileName}
WHERE BOARD_AT_NO = #{fileNo}

UPDATE EH_BOARD SET
BOARD_DELETE_FL = 'Y'
WHERE BOARD_NO = #{boardNo}

